import React, { useState , useEffect} from 'react'
import { Redirect } from 'react-router-dom';
import { addItemToCart } from './helper/cardHelper';
import ImageHelper from './helper/ImageHelper';

const Card = ({
    product, addtoCart = true, removefromCart = false
}) => {
    const [redirect , setRedirect] = useState(false);
    const [ count, setCount ] = useState(product.count);


    const cardTitle = product ? product.name : "";
    const cardDesription = product ? product.description : "product not found";
    const cardPrice = product ? product.price : "Coming soon.."

    const addToCart = () => {
        addItemToCart(product, () => setRedirect(true));
      };

    const getARedirect = (redirect) =>{
        if(redirect){
            return ( <Redirect to="/cart" /> )
        }
    }

    const showAddToCart = (addtoCart) =>{
        return (
            (addtoCart &&  <button
            onClick={addtoCart}
            className="btn btn-block btn-outline-success mt-2 mb-2"
          >
            Add to Cart
          </button>)
        );

    } 

    const showRemoveFromCart = (removefromCart) =>{
        return (
            removefromCart && <button
            onClick={() => {}}
            className="btn btn-block btn-outline-danger mt-2 mb-2"
          >
            Remove from cart
          </button>
        )
    }

        return (
          <div className="card text-white bg-dark border border-info ">
            <div className="card-header lead">{cardTitle}</div>
            <div className="card-body">
            {getARedirect(redirect)}
              <ImageHelper product={product}/>
              <p className="lead bg-success font-weight-normal text-wrap">
                {cardDesription}
              </p>
              <p className="btn btn-success rounded  btn-sm px-4">$ {cardPrice}</p>
              <div className="row">
                <div className="col-12">
                 {showAddToCart(addtoCart)}
                </div>
                <div className="col-12">
                  {showRemoveFromCart(removefromCart)}
                </div>
              </div>
            </div>
          </div>
        );
      };

export default Card;
