import React, { useState } from 'react'
import { Link } from 'react-router-dom';
import { isAuthenticated } from '../auth/helper';
import Base from '../core/Base';
import { createCategory  }    from './helper/adminapicall'


const  AddCategory = () => {

    const [name, setName] = useState("");
    const [error , setError] = useState(false);
    const [success, setSuccess] = useState(false);

    const { user, token} = isAuthenticated();

    const goBack = () =>(
        <div className="mt-5" >
            <Link className="btn btn-sm btn-info mb-3" to="/admin/dashboard">Admin Home</Link>
        </div>
    );

    const handleChange = event =>{
        setError("");
        setName(event.target.value);
    }
    const onSubmit = event =>{
        event.preventDefault();
        setError("");
        setSuccess(false);

        //backend Request fired
        createCategory(user._id, token, {name} )
        .then(data =>{
            if(data.error){
                setError(true);
            }else{
                setError("");
                setSuccess(true);
                setName("");
            }
        })

    }

    const successMsg = () =>{
        if(success){
        return (
            <h4 className="text-success">Category created successFully</h4>
        );
    }
    }
    const warningMsg = () =>{
        if(error){
        return (
            <h4 className="text-success">Category Creation unSuccussfully</h4>
        );
        }
    }

    const myCategoryForm = () =>(
            <form>
                        <div className="form-group">
                            <p className="lead" >Enter the Category</p>
                            <input type="text" onChange={handleChange} value={name} 
                            className="form-control my-3" autoFocus required placeholder="for Ex. Summer"/>
                            <button onClick={onSubmit} className="btn btn-outline-info ">Create Category</button>
                        </div>
                </form>
        );


    return (
        <Base title="Create the category" description="Get the Season Category"
         className="container bg-info pd-4">
        <div className="row bg-white rounded">
            <div className="col-md-8 offset-md-2">
            {successMsg()}
            {warningMsg()}
            {myCategoryForm()}
            {goBack()}
             </div>
        </div>
        </Base>
    )
}
export default AddCategory;
