import React, { useState, useEffect } from 'react'
import Base from "../core/Base"
import { Link } from 'react-router-dom';
import { getCategories, createProduct } from "./helper/adminapicall"
import { isAuthenticated } from "../auth/helper/index"


const  AddProduct = () => {

    const {user, token} = isAuthenticated(); 

    const [values, setValues] = useState({
        name: "",
        description:"",
        price:"",
        stock:"",
        photo: "",
        categories: [],
        category :"",
        loading: false,
        error: "",
        createdProduct: "",
        getaRedirect: false,
        formData : ""
    })
    const { name, description, price, stock, photo, categories, category, loading, error, createdProduct, 
        getaRedirect, formData} = values;

        const preload = () =>{
            getCategories().then(data =>{
                console.log(data);
                if(data.error){
                    setValues({ ...values , error: data.error })
                }else{
                    setValues({ ...values, categories : data, formData: new FormData() });
                    // console.log(categories); // BUG for category
                }
            })
        };

        useEffect(()=>{
                preload();
        }, [])

    const onSubmit = (event) =>{
      event.preventDefault();
      setValues({ ...values , error: "", loading: true});
      createProduct(user._id , token, formData).then(data =>{
        if(data.error){
          setValues({ ...values, error: data.error});
        }else{
          setValues({ ...values, name:"", description:"", price:"", photo: "", stock:"", loading: false,
            createdProduct: data.name
        })
        }
      }) 
    }

    const handleChange = (name) => event => {
        const value = name == "photo" ? event.target.files[0] : event.target.value;
        formData.set(name , value);
        setValues({ ... values, [name] : value});

    }

    const successMsg = () =>(
      <div className="alert alert-success mt-3" style={ { display: createdProduct  ? "" : "none"} }>
        <h4>{createdProduct} createdProduct successfully</h4>
      </div>
    )

    const warningMsg = () =>(
      <div className="alert alert-success mt-3" style={ { display: createdProduct  ? "none" : ""} }>
        <h4>Product Creation Failed </h4>
      </div>
    )

    const createProductForm = () => (
        <form >
          <span>Post photo</span>
          <div className="form-group">
            <label className="btn btn-block btn-success">
              <input
                onChange={handleChange("photo")}
                type="file"
                name="photo"
                accept="image"
                placeholder="choose a file"
              />
            </label>
          </div>
          <div className="form-group">
            <input
              onChange={handleChange("name")}
              name="photo"
              className="form-control"
              placeholder="Name"
              value={name}
            />
          </div>
          <div className="form-group">
            <textarea
              onChange={handleChange("description")}
              name="photo"
              className="form-control"
              placeholder="Description"
              value={description}
            />
          </div>
          <div className="form-group">
            <input
              onChange={handleChange("price")}
              type="number"
              value="20"
              className="form-control"
              placeholder="Price"
              value={price}
            />
          </div>
          <div className="form-group">
            <select
              onChange={handleChange("category")}
              className="form-control"
              placeholder="Category"
            >
              <option>Select</option>
              {categories &&
                    categories.map( (cate, index)=>(
                            <option key={index} value={cate._id} >{cate.name}</option>
                    ))
              }
            </select>
          </div>
          <div className="form-group">
            <input
              onChange={handleChange("stock")}
              type="number"
              className="form-control"
              placeholder="Quantity"
              value={stock}
            />
          </div>

          <button type="submit" onClick={onSubmit} className="btn btn-outline-success mb-3">
            Create Product
          </button>
        </form>
      );

    return (
        <Base title="Add Products" description="Product CreationSection" className="container bg-info p-4">
        <Link className="btn btn-md btn-dark mb-3">
            Admin Home
        </Link>
            <div className="row bg-dark text-white rounded">
                <div className="col-md-8 offset-md-2" >
                {successMsg()} 
                {warningMsg()}
                {createProductForm()} 
                </div>
            </div>
        </Base>
    )
};
export default AddProduct;
