import React, { useState, useEffect } from 'react'
import Base from "../core/Base"
import { Link } from 'react-router-dom'
import { isAuthenticated } from '../auth/helper';
import { getProducts , deleteProduct} from "./helper/adminapicall"

const ManageProducts = () => {

const [product , setProduct] = useState([]);

const {user, token} = isAuthenticated();

const preload =()=>{
    getProducts()
    .then(data =>{
        if(data.error){
            console.log(data.error+" Get all product failed");
        }else{
            setProduct(data);
        }
    })
    .catch(err =>{
        console.log(err+" Get ALl product failed");
    })
}
    useEffect(() => {
        preload();
    }, [])

    const deleteThisProduct = (productId) =>{
        deleteProduct(productId ,user._id, token).then(data =>{
            if(data.error){
                console.log(data.error+" Delete product failed");
            }else{
                preload();
            }
        }) 
    }

    return (
        <Base title="Welcome admin" description="Manage products here">
      <h2 className="mb-4">All products:</h2>
      <Link className="btn btn-info" to={`/admin/dashboard`}>
        <span className="">Admin Home</span>
      </Link>
      <div className="row">
        <div className="col-12">
          <h2 className="text-center text-white my-3">Total {product.length} products</h2>

            {product.map( (product,index) =>{
                    return (
          <div key={index} className="row text-center mb-2 ">
            <div className="col-4">
              <h3 className="text-white text-left">{product.name}</h3>
              {/* <image src={product.photo} /> */}
            </div>
            <div className="col-4">
              <Link
                className="btn btn-success"
                to={`/admin/product/update/${product._id}`}
              >
                <span className="">Update</span>
              </Link>
            </div>
            <div className="col-4">
              <button onClick={() => {
                  deleteThisProduct(product._id);
              }} className="btn btn-danger">
                Delete
              </button>
            </div>
          </div>
            );
        })}
        </div>
      </div>
    </Base>
    )
}

export default ManageProducts;
