import React, { useState } from "react";
import Base from "../core/Base";
import { Link } from "react-router-dom";
import { signup } from "../auth/helper";

const SignUp = () => {
  const [values, setValues] = useState({
    name: "",
    email: "",
    password: "",
    error: "",
    success: false,
  });

  const { name, email, password, error, success } = values;

  const handleChange = (name) => (event) => {
    setValues({ ...values, error: false, [name]: event.target.value });
  };

  const successMessage = () => {
    return (
      <div
        className="alert alert-success"
        style={{ display: success ? "" : "none" }}
      >
        Now Account was created successfully! Plese Login Here
        <Link to="/signin"></Link>
      </div>
    );
  };
  const errorMessage = () => {
    return (
      <div
        className="alert alert-danger"
        style={{ display: error ? "" : "none" }}
      >
        <p>{error}</p>
      </div>
    );
  };

  const onSubmit = (event) => {
    event.preventDefault();
    setValues({ ...values, error: false });
    signup({ name, email, password })
      .then((data) => {
        if (data.error) {
          setValues({ ...values, error: data.error, success: false });
        } else {
          setValues({
            ...values,
            error: "",
            success: true,
            name: "",
            email: "",
            password: "",
          });
        }
      })
      .catch(console.log("Error in SignUp Submit"));
  };

  const signUpform = () => {
    return (
      <div className="row">
        <div className="col-md-7 offset-sm-3 text-left">
          <form>
            <div className="form-group">
              <label className="text-light">Name</label>
              <input
                onChange={handleChange("name")}
                placeholder="Enter Name"
                className="form-control"
                type="text"
                value={name}
              ></input>
            </div>
            <div className="form-group">
              <label className="text-light">Email</label>
              <input
                onChange={handleChange("email")}
                placeholder="Enter Email"
                className="form-control"
                type="email"
                value={email}
              ></input>
            </div>
            <div className="form-group">
              <label className="text-light">Password</label>
              <input
                onChange={handleChange("password")}
                placeholder="Enter Password"
                className="form-control"
                type="password"
                value={password}
              ></input>
            </div>
            <button onClick={onSubmit} className="btn btn-success btn-block">
              Submit
            </button>
          </form>
        </div>
      </div>
    );
  };

  return (
    <Base title="SignUp Page" description="Page for user To signUp">
      <p className="text-light text-center">{JSON.stringify(values)}</p>
      {successMessage}
      {errorMessage}
      {signUpform()}
    </Base>
  );
};

export default SignUp;
