import React, {useState , useEffect} from 'react'
import "../styles.css"
import { API } from "../backend.js"
import Base from "./Base"
import Card from './card'
import userEvent from '@testing-library/user-event'
import { loadCart } from './helper/cardHelper'


const Cart =() => {

        const [products , setProducts] = useState([]);

        useEffect(()=>{
                setProducts(loadCart())
        },[])

    const loadAllProducts = () =>{
        return (
            <div className=""> <h3 className="text-white">This section is load the product</h3>
            {products.map( (product , index) =>{
                <Card 
                    key={index}
                    product = {product}
                    addtoCart = {false}
                     removefromCart = {true}
                />
            })}
            </div>
        )
    }

    const loadCheckout = () =>{
        return (
            <div className=""> <h3 className="text-white">Ready for Check Out</h3></div>
        )
    }

    return (
        <Base title="Product In You Cart" description="Ready to Checkout" >
            <div className="row">
                <div className="col-6" >{loadAllProducts()}</div>
                <div className="col-6" >{loadCheckout()}</div>
            </div>
            </Base>
    )
}
export default Cart;
