import React, {useState , useEffect} from 'react'
import "../styles.css"
import { API } from "../backend.js"
import Base from "./Base"
import Card from './card'
import userEvent from '@testing-library/user-event'
import { loadCart } from './helper/cardHelper'
import Paymentb from "./paymentb"

const Cart =() => {

        const [products , setProducts] = useState([]);
        const [reload , setReload] = useState(false);

        useEffect(()=>{
                setProducts(loadCart())
        },[reload])

    const loadAllProducts = (products) =>{
        return (
            <div className="text-center " > <h3 className="text-white">This section is load the product</h3>
            {products.map( (product , index) =>(
                <Card 
                    key={index}
                    product = {product}
                    addtoCart = {false}
                    removefromCart = {true}
                    setReload = {setReload}
                    reload ={reload}
                />
             ))}
            </div>
        )
    }

    const loadCheckout = () =>{
        return (
            <div className=""> <h3 className="text-white">{Paymentb}</h3></div>
        )
    }

    return (
        <Base title="Product In You Cart" description="Ready to Checkout" >
            <div className="row">
                <div className="col-6" >{products.length >0 ? loadAllProducts(products): 
                (<h3>You don't have any product in cart</h3>)}</div>
                <div className="col-6" >{loadCheckout()}</div>
            </div>
            </Base>
    )
}
export default Cart;
