import React, {useState, useEffect} from 'react'
import { Link } from 'react-router-dom';
import { isAuthenticated } from "../auth/helper/index";
import Base from '../core/Base';
import { deleteCategory, getCategories } from './helper/adminapicall';


const ManageCategories = () => {

    const [category , setCategory] = useState([]);

    const {user, token} = isAuthenticated();

    const preCateyload =()=>{
        getCategories()
        .then(data =>{
            if(data.error){
                console.log(data.error+" Get all Categories failed");
            }else{
                setCategory(data);
            }
        })
        .catch(err =>{
            console.log(err+" Get ALl product failed");
        })
    }
        useEffect(() => {
            preCateyload();
        }, [])

        const deleteThisCategory = (categoryId) =>{
            deleteCategory(categoryId ,user._id, token).then(data =>{
                if(data.error){
                    console.log(data.error+" Delete Category failed");
                }else{
                    preCateyload();
                }
            }) 
        }

        return (
            <Base title="Welcome admin" description="Manage products here">
          <h2 className="mb-4">All Category:</h2>
          <Link className="btn btn-info" to={`/admin/dashboard`}>
            <span className="">Admin Home</span>
          </Link>
          <div className="row">
            <div className="col-12">
              <h2 className="text-center text-white my-3">Total {category.length} products </h2>

                {category.map( (cateyItem,index) =>{
                        return (
              <div key={index} className="row text-center mb-2 ">
                <div className="col-4">
                  <h3 className="text-white text-left">{cateyItem.name}</h3>
                  {/* <image src={product.photo} /> */}
                </div>
                <div className="col-4">
                  <Link
                    className="btn btn-success"
                    to={`/admin/product/update/${cateyItem._id}`}
                  >
                    <span className="">Update</span>
                  </Link>
                </div>
                <div className="col-4">
                  <button onClick={() => {
                      deleteCategory(cateyItem._id);
                  }} className="btn btn-danger">
                    Delete
                  </button>
                </div>
              </div>
                );
            })}
            </div>
          </div>
        </Base>
        )
    }

    export default ManageCategories;
