import React, {useState} from 'react'
import Base from "../core/Base.js"
import { Link, Redirect } from "react-router-dom"

import {signin, authenticate ,isAuthenticated} from "../auth/helper"
const SignIn = () =>{

    const Signin = () =>{

        const[values, setValues] = useState({
            email : "",
            password: "",
            error: "",
            loading: false,
            didRedirect: false

        })

    }

    const {email, password, error, loading, didRedirect} = values;

    const handleChange = (name) => (event) => {
        setValues({ ...values, error: false, [name]: event.target.value });
      };


      const successMessage = () => {
        return (
          <div className="row">
            <div className="col-md-6 offset-sm-3 text-left">
              <div
                className="alert alert-success"
                style={{ display: success ? "" : "none" }}
              >
                New account was created successfully. Please{" "}
                <Link to="/signin">Login Here</Link>
              </div>
            </div>
          </div>
        );
      };

      const errorMessage = () => {
        return (
          <div className="row">
            <div className="col-md-6 offset-sm-3 text-left">
              <div
                className="alert alert-danger"
                style={{ display: error ? "" : "none" }}
              >
                {error}
              </div>
            </div>
          </div>
        );
      };

      const onSubmit = event => {
          event.preventDefault();
          setValues({ ...values, error: false, loading: false})
          signin({email, password})
          .then(data =>{
                if(data.error){
                    setValues({...values, error: data.error, loading:false})
                }
                else{
                    authenticate(data, ()=>{
                        setValues({ ...values, didRedirect: true })
                    })
                    .catch(console.log("Sign In request fail"))
                }
          })
          .catch()
      }

    const { user } = isAuthenticated();

    const signInform = () =>{
        return (
            <div className="row" >
                <div className="col-md-7 offset-sm-3 text-left" >
                    <form>
                        <div className="form-group">
                            <label className="text-light">Email</label>
                            <input onChange={handleChange("email")} value={email} placeholder="Enter Email" className="form-control" type="email"></input>

                        </div>
                        <div className="form-group">
                            <label className="text-light">Password</label>
                            <input onChange={handleChange("password")} value={password} placeholder="Enter Password" className="form-control" type="password"></input>
                        </div>
                        <button onClick={onSubmit} className="btn btn-success btn-block">Submit</button>
                    </form>
                </div>
            </div>
        )
    }


    return (
        <Base  title="Signin Page" description="Page for user To sign In">
            {signInform()}
        </Base>
    )
} 
export default SignIn;
